package com.example.valorapp
import android.content.Context
import android.content.DialogInterface
import android.os.Bundle
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.valorapp.databinding.ActivityUserlistBinding
import com.google.android.material.dialog.MaterialAlertDialogBuilder

class UserlistActivity : AppCompatActivity(), OnClickListener {

    private lateinit var binding: ActivityUserlistBinding
    private lateinit var userAdapter: UserAdapter
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager



    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityUserlistBinding.inflate(layoutInflater)
        setContentView(binding.root)
        // INICIALIZAR
        userAdapter = UserAdapter(getUsers(), this)
        linearLayoutManager = LinearLayoutManager(this)
        // CONFIGURADO EL RECYCLE
        binding.recycleView.apply {
            layoutManager =linearLayoutManager
            adapter = userAdapter
        }

    }

    private fun getUsers(): MutableList<User>{
        val users= mutableListOf<User>()
        val benja = User(1,"Benjam√≠n","Guajardo","data:image/jpeg;base64,");
        val nico = User(2,"Nicol√°s","Canales", "https://media.licdn.com/dms/image/C5603AQExyrZBcecs2A/profile-displayphoto-shrink_800_800/0/1517482339047?e=1718841600&v=beta&t=MfNImFNumq_lek4YfGk39mtJ3-WE0mwrD65w2SSGBXI");
        val jairo = User(3,"Jairo","Mar√≠n","https://static.wikia.nocookie.net/doblaje/images/4/42/William%2BEtty-Judas%2BIscariot.JPG/revision/latest?cb=20180115173212&path-prefix=es");
        users.add(benja)
        users.add(nico)
        users.add(jairo)
        return users
    }

    override fun onClick(user: User, position: Int) {
        Toast.makeText(this,"$position: ${user.getFullName()}", Toast.LENGTH_SHORT).show()
    }
}